```js[nuxt.config.js]
export default {
	// ...
	sitemap: {
		hostname: 'host_of_the_blog',

		// Returns an array with all the routes of our site
		routes() {
			const { getRoutes } = require('./utils/sitemap');

			// The "./dist" directory is where all the Nuxt generated files are saved
			return getRoutes('./dist');
		},
		gzip: true
